#!/bin/bash
# Sets up the classpath & executes JavaKinesisAsl.java standalone
# 
# Expects JAVA_HOME to be set up to a Java 1.8 home location
#
# - Download the aSpark project (to the location aSPARK_PROJ_HOME)
# - Build aSpark: 
#	mvn install -DskipTests=true -Dcheckstyle.skip 
# - Execute the script from the downloaded aSpark project location (aSPARK_PROJ_HOME)
# 	./sbin/runJavaKinesisWordCountASL.sh [app-name] [stream-name] [endpoint-url]
#

if [ -z "${JAVA_HOME}" ]
then
	echo "Error! JAVA_HOME not set.."
	exit 1
fi

if [[ $# -ne 3 ]]
then
	echo "Usage: ./sbin/runJavaKinesisWordCountASL.sh [app-name] [stream-name] [endpoint-url]"
	exit 1
fi 

aSPARK_PROJ_HOME="$(pwd)"

M2_REPO="${HOME}/.m2/"

